import React from 'react';
import './App.css';
import { runInThisContext } from 'vm';

class App extends React.Component {
  render(){
    return (
      <MeditationSetup></MeditationSetup>
    );
  }
} 

class MeditationSetup extends React.Component{
  constructor(props) {
    super(props);
    this.state = {value: '5'};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('You wish to meditate for: ' + this.state.value);
    event.preventDefault();
  }

  render(){
    return(
      <div>
        <h1>Welcome to some piece of mind</h1>
        <p>To begin please enter the length of time you wish to meditate for:</p>
        <select value={this.state.value} onChange={this.handleChange}>
          <option value="5">5 Minutes</option>
          <option value="10">10 Minutes</option>
          <option value="15">15 Minutes</option>
          <option value="20">20 Minutes</option>
        </select>
        <button clickHandler={this.handleClick}>Proceed</button>
        <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" value={this.state.value} onChange={this.handleChange} />
        </label>
        <input type="submit" value="Submit" />
      </form>
      </div>



    )
  }
}


export default App;
