import React from 'react'
import Countdown from 'react-countdown-now'
import PropTypes from 'prop-types'

class MeditationSetup extends React.Component{
    constructor(props){
        super(props);

        this.updateTime = this.updateTime.bind(this);
        this.startTimer = this.startTimer.bind(this);
    }

    updateTime(e) {
        console.log("minutes updated");
        // this.setState({
        //     minutes: e.target.value,
        // });
        this.props.minutes = e.target.value;

    }

    startTimer(event) {
      console.log('Meditation beginning with: ' + (this.props.medTime / 1000 / 60)+ 'minutes remaining');
      const {isMeditating} = this.props; 
      this.setState({isMeditating: !isMeditating});
      this.props.medTime = (parseInt(this.props.minutes, 10) * 1000 * 60)
        // this.setState({
        //     medTime: (parseInt(this.state.minutes, 10) * 1000 * 60),
        // });
    }

    render(){
        return(
            <div>
                <p>To begin please enter the length of time you wish to meditate for:</p>
                <select value={this.props.minutes} onChange={this.updateTime}>
                    <option value="5">5 Minutes</option>
                    <option value="10">10 Minutes</option>
                    <option value="15">15 Minutes</option>
                    <option value="20">20 Minutes</option>
            </select>
            <button onClick={this.startTimer}>Start</button>
          </div>
        )
    }
}

