import React from 'react'
import Countdown from 'react-countdown-now'
import PropTypes from 'prop-types'

class MeditationSetup extends React.Component{
    constructor(props) {
      super(props);
      this.state = {
        time: 0,
        medTime: 0,
        isMeditating: false,
      };

      this.updateTime = this.updateTime.bind(this);
      this.startTimer = this.startTimer.bind(this);
    }
    updateTime(e) {
        this.setState({
            time: (parseInt(e.target.value, 10) * 1000 * 60)
        });
    }

    startTimer(event) {
      console.log('You wish to meditate for: ' + this.state.medTime);
      //this.setState({isMeditating: true});
      this.setState({medTime: this.state.time});
      event.preventDefault();
    }

    render(){
      return(
        <div>
          <h1>Welcome to some piece of mind</h1>
          <p>To begin please enter the length of time you wish to meditate for:</p>
          {/* <form onSubmit={this.handleSubmit}> */}
            <select value={this.state.medTime} onClick={this.updateTime}>
              <option value="5">5 Minutes</option>
              <option value="10">10 Minutes</option>
              <option value="15">15 Minutes</option>
              <option value="20">20 Minutes</option>
            </select>
            <button onClick={this.startTimer}></button>
          {/* </form> */}
          {/* <Timer time={this.state.medTime}></Timer> */}
          <Countdown date={Date.now() + this.state.medTime} />
        </div>
      )
    }
}

MeditationSetup.propTypes = {
    medTime: PropTypes.number.isRequired
}

