import React from 'react';
import './App.css';
import MeditationSetup from './MeditationSetup.js'
import Countdown from 'react-countdown-now'
import PropTypes from 'prop-types'

class App extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      minutes: "5",
      medTime: 0,
      isMeditating: false,
    };

    this.changeMinutes = this.changeMinutes.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.startTimer = this.startTimer.bind(this);

  }
    // this.props.minutes = e.target.value;

  changeMinutes(event){
    this.setState({
        minutes: event
    });
    console.log(this.state.minutes);
  }

  handleCancel(e){

  }

  startTimer(event) {
    console.log('Meditation beginning with: ' + (this.props.medTime / 1000 / 60)+ 'minutes remaining');
    const {isMeditating} = this.props; 
    this.setState({isMeditating: !isMeditating});
  //   this.props.medTime = (parseInt(this.props.minutes, 10) * 1000 * 60)
      this.setState({
          medTime: (parseInt(this.state.minutes, 10) * 1000 * 60),
      });
    }
  render(){
      const isMeditating = this.state.isMeditating;
    return(
      <div>
        {!this.state.isMeditating && <MeditationSetup changeMinutes={this.changeMinutes} startTimer={this.startTimer}></MeditationSetup>}
        {/* <h1>Welcome to some piece of mind</h1>
        <p>To begin please enter the length of time you wish to meditate for:</p>
          <select value={this.state.minutes} onChange={this.updateTime}>
            <option value="5">5 Minutes</option>
            <option value="10">10 Minutes</option>
            <option value="15">15 Minutes</option>
            <option value="20">20 Minutes</option>
          </select>
          <button onClick={this.startTimer}>Start</button> */}
        {/* </form> */}
        {/* <Timer time={this.state.medTime}></Timer> */}
        {this.state.isMeditating && <Countdown date={Date.now() + this.state.medTime}/> && <button onClick={this.handleCancel}>Cancel</button>}
      </div>
    )
  }
}

export default App;
