import React from 'react';
import Countdown from 'react-countdown-now'

class MeditationTimer extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            completed: false,
        };
        this.preCancel = this.preCancel.bind(this);
    }

    preCancel(e){
        this.props.handleCancel(e);
    }

    render() {
        const Completionist = () => {
            <span>Meditation Complete!</span>
        };
        const renderer = ({ hours, minutes, seconds, completed }) => {

            if (completed) {
                // Render a completed state
                this.setState({
                    completed: true
                });
                return (
                    <div className="jumbotron">
                        <h1>Meditation Completed!</h1>
                    </div>
                    );
            } else {
                // Render a countdown
                this.setState({
                    completed: false
                });
                return (
                    <div className="jumbotron">
                        <h1>Time Remaining</h1>
                        <span>{hours}:{minutes}:{seconds}</span>
                    </div>
                );
            }
        };
        return (
            <div>
                <Countdown className="jumbotron" date={Date.now() + this.props.medTime} renderer={renderer} />
                <button className="" onClick={this.preCancel}>Cancel</button>
            </div>
      )
    }
}



export default MeditationTimer;
